          product = row.ratingCount[0].text;
          regEx = /\(([^)]+)\)/;
          regexMatch = regEx.exec(product);

          productDetails = JSON.parse(regexMatch[1]);
          row.ratingCount = [
            { text: productDetails.ratingCount },
          ];
        }

        // if (row.nameExtended) {
